        // jQuery
// Для кожного елемента з класом header-block-right-dark налаштовано прослуховувач подій, щоб ініціювати такі дії, коли його клацнути:
// Видалити або додати клас dark-theme з елемента з ID body-id.
// Видалити або додати клас dark-bg-theme з елемента з ID body-id.
$('.header-block-right-dark').each(function() {
    $(this).on('click', () => {
        $('#body-id').toggleClass('dark-theme');
        $('#body-id').toggleClass('dark-bg-theme');
    });
});

